function FormatDateToMonthDay(props) {
    const date = props.date;
    if ((typeof date !== 'string' || date === '') && typeof date !== 'number') {
        return <></>;
    }
    let d = new Date(date);
    let dateFormatted = d.getDate() + ' ' + d.toLocaleString('default', {month: 'long'});
    let hour = d.getHours();
    return <>dateFormatted + ' - ' + hour + ':00'</>;
}

function CheckWeatherSVG(props){
    const code = props.code;
    let wIcon = '';
    if (typeof weatherCodes[code] == 'undefined') {
        wIcon = weatherCodes['undefined']['icon'];
    } else {
        wIcon = weatherCodes[code]['icon'];
    }
    return wIcon;
}
